import React from "react";
import { useNavigate } from "react-router-dom";
import pizzaHero from "../assets/pizza-hero.jpg"; // ✅ Imported image
import Navbar from "../components/Navbar";

<div style={{ height: "100vh", width: "100vw", overflow: "hidden" }}>
  <Navbar />  {/* ✅ Use single navbar */}
  <div
    style={{
      backgroundImage: `linear-gradient(rgba(0,0,0,0.55), rgba(0,0,0,0.55)), url(${pizzaHero})`,
      backgroundSize: "cover",
      backgroundPosition: "center",
      height: "100vh",
      display: "flex",
      flexDirection: "column",
      justifyContent: "center",
      alignItems: "center",
      textAlign: "center",
      color: "white",
    }}
  >
    {/* Hero content here */}
  </div>
</div>
