// src/pages/Home.jsx
import React from "react";

export default function Home() {
  return (
    <div
      style={{
        textAlign: "center",
        backgroundImage: "pizza-", // ‚úÖ Put this image in your public folder
        backgroundSize: "cover",
        backgroundPosition: "center",
        color: "white",
        height: "80vh",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center"
      }}
    >
      <h1 style={{ fontSize: "50px", textShadow: "2px 2px 8px rgba(0,0,0,0.5)" }}>
        Build Your Perfect Pizza üçï
      </h1>
      <button
        onClick={() => (window.location.href = "/build-pizza")}
        style={{
          marginTop: "20px",
          backgroundColor: "#F37254",
          color: "white",
          border: "none",
          padding: "12px 30px",
          fontSize: "18px",
          borderRadius: "8px",
          cursor: "pointer"
        }}
      >
        Build My Pizza
      </button>
    </div>
  );
}
