// src/components/HeroBackground.jsx
// import React from "react";

export default function HeroBackground({ children }) {
  return (
    <div
      style={{
        position: "relative",
        width: "100%",
        height: "100vh",
        backgroundImage: "url('/pizza-hero.jpg')", // ✅ from public folder
        backgroundSize: "cover",
        backgroundPosition: "center",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        flexDirection: "column",
        color: "white", // ✅ Default text color white
        textAlign: "center",
        overflow: "hidden",
      }}
    >
      {/* Dark overlay to enhance text visibility */}
      <div
        style={{
          position: "absolute",
          width: "100%",
          height: "100%",
          backgroundColor: "rgba(0,0,0,0.5)", // ✅ Dark overlay
          top: 0,
          left: 0,
          zIndex: 1,
        }}
      />

      {/* Content */}
      <div
        style={{
          position: "relative",
          zIndex: 2,
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        {children}
      </div>
    </div>
  );
}
