import React from "react";
import { useNavigate } from "react-router-dom";
import pizzaHero from "../assets/pizza-hero.jpg"; // ‚úÖ Imported image

export default function Home() {
  const navigate = useNavigate();

  return (
    <div
      style={{
        height: "100vh",
        width: "100vw",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* ‚úÖ Navbar */}
      <nav
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          padding: "15px 50px",
          backgroundColor: "rgba(0,0,0,0.6)",
          position: "fixed", // ‚úÖ Fixed so it doesn't push content
          width: "100%",
          top: 0,
          left: 0,
          zIndex: 1000,
        }}
      >
        <h2 style={{ color: "white", fontSize: "28px", fontWeight: "bold" }}>
          Pizzalicious
        </h2>
        <div
          style={{
            display: "flex",
            gap: "20px",
            // flexWrap: "wrap",
          }}
        >
          <a href="/" style={{ color: "white", fontSize: "18px", textDecoration: "none" }}>
            Home
          </a>
          <a href="/build-pizza" style={{ color: "white", fontSize: "18px", textDecoration: "none" }}>
            Build Pizza
          </a>
          <a href="/my-orders" style={{ color: "white", fontSize: "18px", textDecoration: "none" }}>
            My Orders
          </a>
          <a href="/login" style={{ color: "white", fontSize: "18px", textDecoration: "none" }}>
            Login
          </a>
        </div>
      </nav>

      {/* ‚úÖ Hero Section */}
      <div
        style={{
          flex: 1,
          backgroundImage: `linear-gradient(rgba(0,0,0,0.55), rgba(0,0,0,0.55)), url(${pizzaHero})`,
          backgroundSize: "cover",
          backgroundPosition: "center",
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          paddingTop: "60px", // ‚úÖ Offset for navbar
        }}
      >
        <h1
          style={{
            fontSize: "50px",
            marginBottom: "20px",
            color: "white",
            textShadow: "3px 3px 10px rgba(0,0,0,0.8)",
          }}
        >
          Build Your Perfect Pizza üçï
        </h1>
        <p
          style={{
            fontSize: "18px",
            marginBottom: "40px",
            maxWidth: "600px",
            color: "white",
            textShadow: "2px 2px 8px rgba(0,0,0,0.8)",
          }}
        >
          Fresh ingredients, endless choices, and your dream pizza crafted to perfection.
        </p>
        <button
          onClick={() => navigate("/build-pizza")}
          style={{
            padding: "12px 30px",
            fontSize: "18px",
            backgroundColor: "#ff4d4d",
            color: "white",
            border: "none",
            borderRadius: "6px",
            cursor: "pointer",
            transition: "0.3s",
          }}
          onMouseOver={(e) => (e.target.style.backgroundColor = "#e63939")}
          onMouseOut={(e) => (e.target.style.backgroundColor = "#ff4d4d")}
        >
          Build Your Pizza
        </button>
      </div>
    </div>
  );
}
