import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

export default function ResetPassword() {
  const { token } = useParams();
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleReset = async () => {
    try {
      await axios.post(`http://localhost:5000/api/auth/reset-password/${token}`, { password });
      alert("Password reset successful! Please log in.");
      navigate("/login");
    } catch (err) {
      alert("Error: " + (err.response?.data?.message || "Failed to reset password"));
    }
  };

  return (
    <div style={{ background: "lightyellow", height: "100vh", textAlign: "center", paddingTop: "50px" }}>
      <h1>Reset Your Password</h1>
      <input
        type="password"
        placeholder="Enter new password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        style={{ padding: "10px", marginTop: "20px" }}
      />
      <br />
      <button onClick={handleReset} style={{ marginTop: "20px", padding: "10px 20px" }}>
        Reset Password
      </button>
    </div>
  );
}
