import React from "react";
import { useNavigate } from "react-router-dom";
import pizzaHero from "../assets/pizza-hero.jpg"; // ‚úÖ Import hero image

export default function Home() {
  const navigate = useNavigate();

  return (
    <div
      style={{
        backgroundImage: `linear-gradient(
          rgba(0, 0, 0, 0.5),
          rgba(0, 0, 0, 0.5)
        ), url(${pizzaHero})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        height: "100vh",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        color: "white",
      }}
    >
      <h1 style={{ fontSize: "50px", marginBottom: "20px" }}>
        Build Your Perfect Pizza üçï
      </h1>
      <p style={{ fontSize: "18px", marginBottom: "40px" }}>
        Fresh ingredients, endless choices, and your dream pizza crafted to perfection.
      </p>
      <button
        onClick={() => navigate("/build-pizza")}
        style={{
          padding: "12px 30px",
          fontSize: "18px",
          backgroundColor: "#ff4d4d",
          color: "white",
          border: "none",
          borderRadius: "6px",
          cursor: "pointer",
          transition: "0.3s",
        }}
        onMouseOver={(e) => (e.target.style.backgroundColor = "#e63939")}
        onMouseOut={(e) => (e.target.style.backgroundColor = "#ff4d4d")}
      >
        Build Your Pizza
      </button>
    </div>
  );
}
