import React from "react";
import { Link } from "react-router-dom";

export default function Navbar() {
  return (
    <nav
      style={{
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        padding: "15px 40px",
        backgroundColor: "rgba(0, 0, 0, 0)", // ✅ Fully transparent
        position: "fixed",
        width: "100%",
        top: 0,
        left: 0,
        zIndex: 1000,
      }}
    >
      {/* Logo */}
      <h2 style={{ color: "white", fontSize: "28px", fontWeight: "bold", margin: 0 }}>
        Pizzalicious
      </h2>

      {/* Navigation Links */}
      <div
        style={{
          display: "flex",
          gap: "40px", // ✅ Enough space so Login doesn’t go out of frame
          marginRight: "30px",
        }}
      >
        <Link to="/" style={linkStyle}>Home</Link>
        <Link to="/build-pizza" style={linkStyle}>Build Pizza</Link>
        <Link to="/my-orders" style={linkStyle}>My Orders</Link>
        <Link to="/login" style={linkStyle}>Login</Link>
      </div>
    </nav>
  );
}

const linkStyle = {
  color: "white",
  fontSize: "18px",
  textDecoration: "none",
};
