import React, { useState, useEffect } from "react";
import axios from "axios";
import FakeRazorpayModal from "../components/FakeRazorpayModal";

export default function BuildPizza() {
  const [selection, setSelection] = useState({ base: null, sauce: null, cheese: null, veggie: [] });
  const [totalPrice, setTotalPrice] = useState(0);
  const [showPaymentModal, setShowPaymentModal] = useState(false);

  const token = localStorage.getItem("token");

  const handleSelect = (category, item) => {
    if (category === "veggie") {
      setSelection((prev) => {
        const veggies = prev.veggie.includes(item.name)
          ? prev.veggie.filter((v) => v !== item.name)
          : [...prev.veggie, item.name];
        return { ...prev, veggie: veggies };
      });
    } else {
      setSelection((prev) => ({ ...prev, [category]: item.name }));
    }
  };

  const calculatePrice = () => {
    let total = 0;
    if (selection.base) total += inventory.base.find((b) => b.name === selection.base)?.price || 0;
    if (selection.sauce) total += inventory.sauce.find((s) => s.name === selection.sauce)?.price || 0;
    if (selection.cheese) total += inventory.cheese.find((c) => c.name === selection.cheese)?.price || 0;
    selection.veggie.forEach((v) => {
      total += inventory.veggie.find((veg) => veg.name === v)?.price || 0;
    });
    setTotalPrice(total);
  };

  useEffect(() => {
    calculatePrice();
  }, [selection]);

  // ✅ Open Fake Razorpay Modal
  const placeOrder = () => {
    if (!token) return alert("Please log in first!");

    if (!selection.base || !selection.sauce || !selection.cheese || selection.veggie.length === 0) {
      return alert("Please complete your pizza selection!");
    }

    setShowPaymentModal(true);
  };

  // ✅ Handle Payment Success
  const handlePaymentSuccess = async () => {
    setShowPaymentModal(false);

    // Build items array
    const items = [];
    const addItem = (category, name) => {
      const item = inventory[category].find((i) => i.name === name);
      if (item) items.push({ category, name: item.name, price: item.price });
    };

    addItem("base", selection.base);
    addItem("sauce", selection.sauce);
    addItem("cheese", selection.cheese);
    selection.veggie.forEach((v) => addItem("veggie", v));

    try {
      await axios.post(
        "http://localhost:5000/api/orders",
        { items, totalPrice },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      alert("✅ Payment simulated & order placed successfully!");
      setSelection({ base: null, sauce: null, cheese: null, veggie: [] });
      setTotalPrice(0);
    } catch (err) {
      console.error(err);
      alert("❌ Failed to place order");
    }
  };

  return (
    <div style={{ textAlign: "center" }}>
      <h1>Build Your Pizza</h1>
      <h2>Total: ₹{totalPrice}</h2>
      
      {Object.keys(inventory).map((category) => (
        <div key={category} style={{ margin: "20px 0" }}>
          <h3>{category.toUpperCase()}</h3>
          {inventory[category].map((item) => (
            <label key={item.name} style={{ margin: "0 10px" }}>
              <input
                type={category === "veggie" ? "checkbox" : "radio"}
                name={category}
                onChange={() => handleSelect(category, item)}
                checked={
                  category === "veggie"
                    ? selection.veggie.includes(item.name)
                    : selection[category] === item.name
                }
              />
              {item.name} (₹{item.price})
            </label>
          ))}
        </div>
      ))}

      <button onClick={placeOrder} style={{ padding: "10px 20px" }}>
        Place Order
      </button>

      {showPaymentModal && (
        <FakeRazorpayModal
          amount={totalPrice}
          onSuccess={handlePaymentSuccess}
          onClose={() => setShowPaymentModal(false)}
        />
      )}
    </div>
  );
}
