// Example: Home.jsx
import React from "react";
import pizzaHero from "../assets/pizza-hero.jpg"; // ‚úÖ Import from src

export default function Home() {
  return (
    <div
      style={{
        backgroundImage: `linear-gradient(rgba(0,0,0,0.5), rgba(0,0,0,0.5)), url(${pizzaHero})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        minHeight: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        color: "white",
        textAlign: "center",
      }}
    >
      <div>
        <h1 style={{ fontSize: "50px" }}>Build Your Perfect Pizza üçï</h1>
        <p>Fresh ingredients, endless choices, and your dream pizza crafted to perfection.</p>
        <button
          style={{
            marginTop: "20px",
            padding: "12px 30px",
            backgroundColor: "#f37254",
            border: "none",
            color: "white",
            fontSize: "18px",
            borderRadius: "8px",
            cursor: "pointer",
          }}
        >
          Build Pizza
        </button>
      </div>
    </div>
  );
}
