// src/pages/Home.jsx
import React from "react";
import { useNavigate } from "react-router-dom";

export default function Home() {
  const navigate = useNavigate();

  const handleBuildPizza = () => {
    navigate("/build-pizza");
  };

  return (
    <div
      style={{
        textAlign: "center",
        backgroundImage: "url('/pizza-hero.jpg')", // ‚úÖ From public folder
        backgroundSize: "cover",
        backgroundPosition: "center",
        height: "100vh",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        color: "white",
        position: "relative",
      }}
    >
      {/* Dark overlay for better readability */}
      <div
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          backgroundColor: "rgba(0,0,0,0.5)",
          zIndex: 0,
        }}
      ></div>

      {/* Main Content */}
      <div style={{ zIndex: 1 }}>
        <h1
          style={{
            fontSize: "50px",
            fontWeight: "bold",
            textShadow: "2px 2px 10px rgba(0,0,0,0.8)",
            marginBottom: "20px",
          }}
        >
          Build Your Perfect Pizza üçï
        </h1>
        <p
          style={{
            fontSize: "20px",
            textShadow: "1px 1px 5px rgba(0,0,0,0.8)",
            marginBottom: "30px",
          }}
        >
          Fresh ingredients, endless choices, and your dream pizza crafted to perfection.
        </p>

        <button
          onClick={handleBuildPizza}
          style={{
            padding: "12px 30px",
            backgroundColor: "#F37254",
            border: "none",
            borderRadius: "8px",
            color: "white",
            fontSize: "18px",
            fontWeight: "bold",
            cursor: "pointer",
            boxShadow: "0 4px 10px rgba(0,0,0,0.5)",
          }}
        >
          Build Pizza
        </button>
      </div>
    </div>
  );
}
