import React from "react";
import { useNavigate } from "react-router-dom";
import Navbar from "../components/Navbar";
import pizzaHero from "../assets/pizza-hero.jpg";

export default function Home() {
  const navigate = useNavigate();

  return (
    <div style={{ height: "100vh", overflow: "hidden" }}>
      <Navbar />

      <div
        style={{
          backgroundImage: `linear-gradient(rgba(0,0,0,0.55), rgba(0,0,0,0.55)), url(${pizzaHero})`,
          backgroundSize: "cover",
          backgroundPosition: "center",
          height: "100vh",
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "0 20px",
        }}
      >
        <h1
          style={{
            fontSize: "50px",
            textShadow: "2px 2px 8px rgba(0,0,0,0.8)", // ‚úÖ Strong shadow for visibility
            marginBottom: "20px",
          }}
        >
          Build Your Perfect Pizza üçï
        </h1>
        <p
          style={{
            fontSize: "20px",
            maxWidth: "600px",
            textShadow: "2px 2px 6px rgba(0,0,0,0.7)", // ‚úÖ White text with strong shadow
            marginBottom: "30px",
            color: "white",
          }}
        >
          Fresh ingredients, endless choices, and your dream pizza crafted to perfection.
        </p>
        <button
          onClick={() => navigate("/build-pizza")}
          style={{
            padding: "12px 30px",
            fontSize: "18px",
            backgroundColor: "#FF4C4C",
            color: "white",
            border: "none",
            borderRadius: "6px",
            cursor: "pointer",
            transition: "0.3s",
          }}
          onMouseOver={(e) => (e.target.style.backgroundColor = "#e63e3e")}
          onMouseOut={(e) => (e.target.style.backgroundColor = "#FF4C4C")}
        >
          Build Your Pizza
        </button>
      </div>
    </div>
  );
}
