import React from "react";
import "../App.css"; // ‚úÖ Import global CSS

export default function Home() {
  return (
    <div className="page-container">
      <div className="page-content">
        <h1 style={{ fontSize: "50px", textShadow: "2px 2px 8px rgba(0,0,0,0.7)" }}>
          Build Your Perfect Pizza üçï
        </h1>
        <p style={{ fontSize: "20px", marginTop: "10px" }}>
          Fresh ingredients. Endless combinations. Make it yours.
        </p>
        <button
          style={{
            marginTop: "20px",
            padding: "12px 30px",
            fontSize: "18px",
            backgroundColor: "#F37254",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
          onClick={() => (window.location.href = "/build-pizza")}
        >
          Start Building ‚Üí
        </button>
      </div>
    </div>
  );
}
