import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function BuildPizza() {
  const [selection, setSelection] = useState({ base: null, sauce: null, cheese: null, veggie: [] });
  const [totalPrice, setTotalPrice] = useState(0);
  const navigate = useNavigate(); // ✅ For redirecting

  // ✅ Check token on page load
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      alert("Please log in to build your pizza!");
      navigate("/login"); // Redirect to login
    }
  }, [navigate]);

  // ... your handleSelect, calculatePrice, placeOrder functions here
}
