import React, { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

export default function Register() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleRegister = async (e) => {
    e.preventDefault(); // ✅ Prevent page refresh

    try {
      const res = await axios.post("http://localhost:5000/api/auth/register", {
        name,
        email,
        password
      });

      alert(res.data.message || "Registration successful! Please check your email for verification.");

      // ✅ Redirect to login page
      window.location.href = "/login";

    } catch (err) {
      alert("Registration failed: " + (err.response?.data?.message || "Server error"));
    }
  };

  return (
    <div style={{
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      height: "80vh",
      background: "#fff8f0"
    }}>
      <form 
        onSubmit={handleRegister}
        style={{
          background: "white",
          padding: "40px",
          borderRadius: "12px",
          boxShadow: "0 4px 15px rgba(0,0,0,0.2)",
          width: "350px",
          textAlign: "center"
        }}
      >
        <h2 style={{ marginBottom: "20px", color: "#F37254" }}>Register</h2>

        <input
          type="text"
          placeholder="Full Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          style={{
            margin: "10px 0",
            padding: "10px",
            width: "100%",
            border: "1px solid #ccc",
            borderRadius: "6px"
          }}
        />

        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          style={{
            margin: "10px 0",
            padding: "10px",
            width: "100%",
            border: "1px solid #ccc",
            borderRadius: "6px"
          }}
        />

        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{
            margin: "10px 0",
            padding: "10px",
            width: "100%",
            border: "1px solid #ccc",
            borderRadius: "6px"
          }}
        />

        <button 
          type="submit"
          style={{
            background: "#F37254",
            color: "white",
            border: "none",
            padding: "10px 20px",
            borderRadius: "8px",
            cursor: "pointer",
            marginTop: "10px",
            width: "100%"
          }}
        >
          Register
        </button>

        <p style={{ marginTop: "15px" }}>
          Already a customer?{" "}
          <Link to="/login" style={{ color: "#F37254" }}>
            Login
          </Link>
        </p>
      </form>
    </div>
  );
}
