import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const inventory = {
  base: [
    { name: "Thin Crust", price: 150 },
    { name: "Cheese Burst", price: 150 },
    { name: "Pan Crust", price: 100 },
  ],
  sauce: [
    { name: "Tomato Basil", price: 30 },
    { name: "Pesto Sauce", price: 30 },
    { name: "Alfredo White Sauce", price: 30 },
  ],
  cheese: [
    { name: "Mozzarella", price: 50 },
    { name: "Cheddar", price: 50 },
    { name: "Parmesan", price: 50 },
  ],
  veggie: [
    { name: "Mushrooms", price: 60 },
    { name: "Onions", price: 20 },
    { name: "Capsicum", price: 20 },
  ],
};

export default function BuildPizza() {
  const [selection, setSelection] = useState({ base: null, sauce: null, cheese: null, veggie: [] });
  const [totalPrice, setTotalPrice] = useState(0);
  const navigate = useNavigate();

  // ✅ Check if user is logged in
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      alert("Please log in to build your pizza!");
      navigate("/login");
    }
  }, [navigate]);

  // ✅ Handle selection of pizza options
  const handleSelect = (category, item) => {
    if (category === "veggie") {
      setSelection((prev) => {
        const veggies = prev.veggie.includes(item.name)
          ? prev.veggie.filter((v) => v !== item.name)
          : [...prev.veggie, item.name];
        return { ...prev, veggie: veggies };
      });
    } else {
      setSelection((prev) => ({ ...prev, [category]: item.name }));
    }
  };

  // ✅ Calculate total price
  const calculatePrice = () => {
    let total = 0;
    if (selection.base) total += inventory.base.find((b) => b.name === selection.base)?.price || 0;
    if (selection.sauce) total += inventory.sauce.find((s) => s.name === selection.sauce)?.price || 0;
    if (selection.cheese) total += inventory.cheese.find((c) => c.name === selection.cheese)?.price || 0;
    selection.veggie.forEach((v) => {
      total += inventory.veggie.find((veg) => veg.name === v)?.price || 0;
    });
    setTotalPrice(total);
  };

  useEffect(() => {
    calculatePrice();
  }, [selection]);

  const placeOrder = async () => {
  const token = localStorage.getItem("token");
  if (!token) {
    alert("Please log in first!");
    return;
  }

  // Ensure all selections are made
  if (!selection.base || !selection.sauce || !selection.cheese || selection.veggie.length === 0) {
    alert("Please complete your pizza selection!");
    return;
  }

  // ✅ Build items array with full details
  const items = [];

  const baseItem = inventory.base.find((b) => b.name === selection.base);
  if (baseItem) items.push({ category: "base", name: baseItem.name, price: baseItem.price });

  const sauceItem = inventory.sauce.find((s) => s.name === selection.sauce);
  if (sauceItem) items.push({ category: "sauce", name: sauceItem.name, price: sauceItem.price });

  const cheeseItem = inventory.cheese.find((c) => c.name === selection.cheese);
  if (cheeseItem) items.push({ category: "cheese", name: cheeseItem.name, price: cheeseItem.price });

  selection.veggie.forEach((v) => {
    const vegItem = inventory.veggie.find((veg) => veg.name === v);
    if (vegItem) items.push({ category: "veggie", name: vegItem.name, price: vegItem.price });
  });

  try {
    // 1️⃣ Simulate Razorpay Popup
    const simulatePayment = () => {
      return new Promise((resolve) => {
        const confirmed = window.confirm(
          `Simulated Razorpay Payment\n\nAmount: ₹${totalPrice}\n\nClick OK to confirm payment.`
        );
        resolve(confirmed);
      });
    };

    const paymentSuccess = await simulatePayment();

    if (!paymentSuccess) {
      alert("Payment cancelled!");
      return;
    }

    alert("✅ Payment simulated successfully!");

    // 2️⃣ Create the order in backend
    const res = await axios.post(
      "http://localhost:5000/api/orders",
      { items, totalPrice }, 
      { headers: { Authorization: `Bearer ${token}` } }
    );

    console.log("✅ Order response:", res.data);
    alert("Order placed successfully!");

    // 3️⃣ Reset selection
    setSelection({ base: null, sauce: null, cheese: null, veggie: [] });
    setTotalPrice(0);

  } catch (err) {
    console.error("❌ Order error:", err.response?.data || err.message);
    alert("Error placing order");
  }
};

  return (
    <div style={{ textAlign: "center" }}>
      <h1>Build Your Pizza</h1>
      <h2>Total: ₹{totalPrice}</h2>
      {Object.keys(inventory).map((category) => (
        <div key={category} style={{ margin: "20px 0" }}>
          <h3>{category.toUpperCase()}</h3>
          {inventory[category].map((item) => (
            <label key={item.name} style={{ margin: "0 10px" }}>
              <input
                type={category === "veggie" ? "checkbox" : "radio"}
                name={category}
                onChange={() => handleSelect(category, item)}
              />
              {item.name} (₹{item.price})
            </label>
          ))}
        </div>
      ))}
      <button onClick={placeOrder} style={{ padding: "10px 20px" }}>
        Place Order
      </button>
    </div>
  );
}
