import "../App.css"; // ‚úÖ Import global CSS

export default function Home() {
  return (
    <div
      style={{
        textAlign: "center",
        height: "100vh",
        width: "100vw",
        backgroundImage: "url('/pizza-hero.jpg')", // ‚úÖ Public folder access
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        color: "white",
      }}
    >
      <h1 style={{ fontSize: "50px", textShadow: "2px 2px 8px rgba(0,0,0,0.5)" }}>
        Build Your Perfect Pizza üçï
      </h1>
    </div>
  );
}
