// src/App.js
import React from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import BuildPizza from "./pages/BuildPizza";
import Login from "./pages/Login";
import Register from "./pages/Register";
import MyOrders from "./pages/MyOrders";
import AdminDashboard from "./pages/AdminDashboard";
// import Home from "./pages/Home";

export default function Home() {
  return (
    <div
      style={{
        position: "relative",
        height: "100vh",
        width: "100vw",
        overflow: "hidden",
      }}
    >
      {/* üîπ Background Image with Dark Overlay */}
      <div
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          backgroundImage: "url('/pizza-hero.jpg')", // public folder
          backgroundSize: "cover",
          backgroundPosition: "center",
          backgroundAttachment: "fixed", // üîπ Makes it fixed
          filter: "brightness(0.5)", // üîπ Darkens image for readability
          zIndex: -1,
        }}
      ></div>

      {/* üîπ Hero Text */}
      <div
        style={{
          height: "100%",
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
          color: "white",
          textAlign: "center",
        }}
      >
        <h1 style={{ fontSize: "60px", textShadow: "2px 2px 10px rgba(0,0,0,0.8)" }}>
          Build Your Perfect Pizza üçï
        </h1>
        <p style={{ fontSize: "22px", maxWidth: "600px", marginTop: "20px" }}>
          Fresh ingredients, endless choices, and your dream pizza crafted to perfection.
        </p>
      </div>
    </div>
  );
}
